CREATE TABLE outstations (
    outstationid bigint NOT NULL,
    name character varying(255),
    description text,
    orientation character varying(8),
    speedlimit smallint,
    location geography(Point,4326)
);


ALTER TABLE ONLY outstations ADD CONSTRAINT outstations_pkey PRIMARY KEY (outstationid);

CREATE INDEX outstations_location_idx ON outstations USING gist (location);